  content: "/";
  color: var(--text-light);
  margin-left: 0.5rem;
  font-weight: 300;
}

.breadcrumb-modern a {
  color: var(--text-dark);
  text-decoration: none;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.4rem;
  padding: 0.4rem 0.8rem;
  border-radius: 6px;
}

.breadcrumb-modern a:hover {
  color: var(--primary-color);
  background: rgba(13, 38, 75, 0.05);
}

.breadcrumb-modern a i {
  font-size: 1rem;
}

.breadcrumb-modern .active {
  color: var(--primary-color);
  font-weight: 600;
  padding: 0.4rem 0.8rem;
}

/* Breadcrumb for Dark Backgrounds (Hero Sections) */
.service-detail-header .breadcrumb,
.breadcrumb {
  background: transparent;
  padding: 0;
  margin-bottom: 1.5rem;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 0.5rem;
  list-style: none;
}

.service-detail-header .breadcrumb-item,
.breadcrumb .breadcrumb-item {
  display: flex;
  align-items: center;
  font-size: 0.9rem;
  font-weight: 500;
}

.service-detail-header .breadcrumb-item + .breadcrumb-item::before,
.breadcrumb .breadcrumb-item + .breadcrumb-item::before {
  content: "/";
  color: rgba(255, 255, 255, 0.5);
  margin: 0 0.5rem;
  font-weight: 300;
}

.service-detail-header .breadcrumb-item a {
  color: rgba(255, 255, 255, 0.9);
  text-decoration: none;
  transition: all 0.3s ease;
  padding: 0.4rem 0.8rem;
  border-radius: 6px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
}

.service-detail-header .breadcrumb-item a:hover {
  color: white;
  background: rgba(255, 255, 255, 0.2);
}

.service-detail-header .breadcrumb-item.active {
  color: white;
  font-weight: 600;
  padding: 0.4rem 0.8rem;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 6px;
}

/* CTA Section - Modern Design */
.cta-section-modern {
  background: var(--gradient-1);
  padding: 50px 0;
  margin: 40px 0;
  border-radius: 20px;
  position: relative;
  overflow: hidden;
}

.cta-background-pattern {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg width="100" height="100" xmlns="http://www.w3.org/2000/svg"><defs><pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse"><path d="M 40 0 L 0 0 0 40" fill="none" stroke="rgba(255,255,255,0.05)" stroke-width="1"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)"/></svg>');
  opacity: 0.3;
}

.cta-content {
  position: relative;
  z-index: 1;
}

.cta-content h2 {
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 0.75rem;
}

.cta-content p {
  font-size: 1.1rem;
  opacity: 0.95;
  max-width: 700px;
  margin: 0 auto 1.5rem;
}

.cta-buttons-wrapper {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  max-width: 600px;
  margin: 0 auto;
}

.cta-btn-primary {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  width: 100%;
  background: white;
  color: var(--primary-color);
  padding: 1.25rem 1.75rem;
  border-radius: 12px;
  font-size: 1.05rem;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 5px 20px rgba(0,0,0,0.08);
  border: none;
}

.cta-btn-primary:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 30px rgba(0,0,0,0.15);
  color: var(--primary-color);
}

.cta-btn-primary i:first-child {
  font-size: 1.3rem;
}

.cta-btn-primary i:last-child {
  margin-left: auto;
  transition: transform 0.3s ease;
}

.cta-btn-primary:hover i:last-child {
  transform: translateX(5px);
}

.cta-btn-secondary {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  width: 100%;
  background: white;
  color: var(--primary-color);
  padding: 1.25rem 1.75rem;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  text-decoration: none;
  border: 2px solid rgba(13, 38, 75, 0.1);
  transition: all 0.3s ease;
  box-shadow: 0 5px 20px rgba(0,0,0,0.08);
}

.cta-btn-secondary:hover {
  background: white;
  border-color: var(--primary-color);
  color: var(--primary-color);
  transform: translateY(-5px);
  box-shadow: 0 10px 30px rgba(0,0,0,0.15);
}

.cta-btn-secondary i {
  font-size: 1.2rem;
}

.cta-btn-secondary i:last-child {
  margin-left: auto;
  transition: transform 0.3s ease;
}

.cta-btn-secondary:hover i:last-child {
  transform: translateX(5px);
}

@media (max-width: 768px) {
  .cta-section-modern {
    padding: 40px 0;
    margin: 30px 0;
  }

  .cta-content h2 {
    font-size: 1.75rem;
  }

  .cta-content p {
    font-size: 1rem;
  }

  .cta-btn-primary {
    padding: 1.15rem 1.5rem;
    font-size: 1rem;
  }

  .cta-btn-secondary {
    padding: 1.15rem 1.5rem;
    font-size: 0.95rem;
  }
}

/* Footer */
footer {
  background: var(--footer-bg);
  color: white;
  padding: 60px 0 0;
}

footer h5 {
  font-size: 1.1rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  color: white;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

footer a {
  color: rgba(255, 255, 255, 0.7);
  text-decoration: none;
  transition: all 0.3s ease;
  display: block;
  margin-bottom: 0.75rem;
  font-size: 0.95rem;
}

footer a:hover {
  color: white;
  padding-left: 5px;
}

footer .logo-section {
  margin-bottom: 2rem;
}

footer .logo-section p {
  color: rgba(255, 255, 255, 0.7);
  font-size: 0.9rem;
  line-height: 1.7;
  margin-bottom: 0;
}

.footer-contact {
  background: rgba(0, 0, 0, 0.2);
  padding: 1.5rem 0;
  margin-top: 3rem;
  border-top: 1px solid var(--border-color);
}

.footer-contact p {
  margin: 0;
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.9rem;
}

.footer-contact a {
  display: inline;
  margin: 0 0.5rem;
}

.social-icons {
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
}

.social-icons a {
  display: inline-flex;
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 50%;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  transition: all 0.3s ease;
}

.social-icons a:hover {
  background: var(--primary-color);
  transform: translateY(-3px);
  padding-left: 0;
}

.footer-bottom {
  background: rgba(0, 0, 0, 0.2);
  padding: 1.5rem 0;
  margin-top: 0;
  text-align: center;
  color: rgba(255, 255, 255, 0.6);
  font-size: 0.85rem;
  border-top: 1px solid var(--border-color);
}

/* Testimonials */
.testimonial {
  background: white;
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 5px 20px rgba(0,0,0,0.08);
  margin-bottom: 2rem;
}

.testimonial-text {
