  height: 100%;
  object-fit: cover;
}

.service-detail-header {
  background: var(--gradient-2);
  color: white;
  padding: 80px 0 60px;
  margin-bottom: 3rem;
}

.service-detail-header h1 {
  margin-bottom: 1rem;
}

.service-detail-header .lead {
  font-size: 1.2rem;
  opacity: 0.9;
}

.service-feature {
  padding: 2rem;
  background: white;
  border-radius: 15px;
  box-shadow: 0 3px 15px rgba(0,0,0,0.05);
  margin-bottom: 2rem;
  transition: all 0.3s ease;
}

.service-feature:hover {
  transform: translateY(-5px);
  box-shadow: 0 5px 20px rgba(0,0,0,0.1);
}

.service-feature i {
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: 1rem;
}

.service-feature h4 {
  margin-bottom: 1rem;
  color: var(--dark-color);
}

.btn-outline-light {
  border: 2px solid white;
  color: white;
  padding: 12px 32px;
  font-weight: 600;
  border-radius: 50px;
  background: transparent;
  transition: all 0.3s ease;
